import React, { useState } from "react";
import Square from "./Square";

function Game() {

    const [squares, setSquares] = useState(Array(9).fill(null));

    return (
        <div className="container">
            <div className="game">
                <div className="game-board">
                    <div className="board-row">
                        <Square value={0} />
                        <Square value={1} />
                        <Square value={2} />
                    </div>
                    <div className="board-row">
                        <Square value={3} />
                        <Square value={4} />
                        <Square value={5} />
                    </div>
                    <div className="board-row">
                        <Square value={6} />
                        <Square value={7} />
                        <Square value={8} />
                    </div>
                </div>
            </div>
        </div>
    );
}

function renderSquare(i) {
    return <Square
        value={squares[i]}
        onClick={() => {
            squares[i] = 'X';
        }}
    />;
}

export default Game;
